import React, { createContext, useState } from 'react';
const CarContext = createContext();

const ProviderCar = ({ children }) =>  {

    const [red, setCar ] = useState({red: false, blue: false, yellow });

    const moveCar = (car) => {
        if(car === 'red') {
         setCar(redCar => true);
        }
    }
    const cars = {
        ...CarContext, 
        red,
        moveCar
    };   

    return(
        <CarContext value={ cars }>
        { children }
        </CarContext>
    );

}

export  { CarContext, ProviderCar as Provider };
