import React, { createContext, useState } from 'react';
const CarContext = createContext();

const ProviderCar = ({ children }) =>  {
    //variável e uma função
    const [car, setCar ] = useState({red: false, blue: false, yellow: false });

    const moveCar = (car, side) => {
         setCar({
             ...car,
             [car]: side,
         });
        }
    const cars = {
        ...car,
        moveCar
    };   

    return(
        <CarContext value={ cars }>
        { children }
        </CarContext>
    );

}

export  { CarContext, ProviderCar as Provider };
