import React, { useState } from 'react';
import CarsContext from './CarsContext';

const ProviderCar = ({ children }) =>  {
    //variável e uma função
    const [car, setCar ] = useState({red: false, blue: false, yellow: false });

    const moveCar = (car, side) => {
         setCar({
             ...car,
             [car]: side,
         });
        }
    const cars = {
        car: 'moveCar'
    };   

    return(
        <CarsContext value={ cars }>
        { children }
        </CarsContext>
    );

}

export  { ProviderCar as Provider };
